package Tricky_Naveen;

/**
 * @author Harsh
 * What will be the output when you divide a number by zero?
 * 
 * Arithmetic exception comes on interger value Not on float,double,
 */
public class Divide_number_by_zero {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println(9.0/0);

System.out.println(12.33f/0);

System.out.println(10/0.0);

System.out.println(19.2211d/0);

System.out.println(0.0/0);


System.out.println(0.0/0.0);

	}

}
